export const db = drizzle({ client: pool, schema });

server/googleAuth.ts.content:
import { Strategy as GoogleStrategy } from "passport-google-oauth20";
import passport from "passport";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import connectPg from "connect-pg-simple";
import { storage } from "./storage";

export function getGoogleSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const pgStore = connectPg(session);
  const sessionStore = new pgStore({
    conString: process.env.DATABASE_URL,
    createTableIfMissing: false,
    ttl: sessionTtl,
    tableName: "sessions",
  });
  return session({
    secret: process.env.SESSION_SECRET || 'dev-secret-key-change-in-production',
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: process.env.NODE_ENV === 'production',
      maxAge: sessionTtl,
    },
  });
}

export async function setupGoogleAuth(app: Express) {
  app.set("trust proxy", 1);
  app.use(getGoogleSession());
  app.use(passport.initialize());
  app.use(passport.session());

  // Google OAuth Strategy
  if (process.env.GOOGLE_CLIENT_ID && process.env.GOOGLE_CLIENT_SECRET) {
    passport.use(new GoogleStrategy({
      clientID: process.env.GOOGLE_CLIENT_ID,
      clientSecret: process.env.GOOGLE_CLIENT_SECRET,
      callbackURL: "/api/auth/google/callback",
      scope: ['profile', 'email']
    }, async (accessToken, refreshToken, profile, done) => {
      try {
        // Upsert user in database
        await storage.upsertUser({
          id: profile.id,
          email: profile.emails?.[0]?.value || null,
          firstName: profile.name?.givenName || null,
          lastName: profile.name?.familyName || null,
          profileImageUrl: profile.photos?.[0]?.value || null,
        });

        const user = {
          id: profile.id,
          email: profile.emails?.[0]?.value,
          displayName: profile.displayName,
          firstName: profile.name?.givenName,
          lastName: profile.name?.familyName,
          profileImageUrl: profile.photos?.[0]?.value,
        };

        return done(null, user);
      } catch (error) {
        return done(error, null);
      }
    }));
  }

  passport.serializeUser((user: any, done) => {
    done(null, user);
  });

  passport.deserializeUser((user: any, done) => {
    done(null, user);
  });

  // Google OAuth Routes
  app.get("/api/auth/google", 
    passport.authenticate("google", { 
      scope: ["profile", "email"] 
    })
  );

  app.get("/api/auth/google/callback",
    passport.authenticate("google", { 
      failureRedirect: "/" 
    }),
    (req, res) => {
      res.redirect("/");
    }
  );

  // Logout route
  app.get("/api/auth/logout", (req, res) => {
    req.logout(() => {
      res.redirect("/");
    });
  });
}

export const isAuthenticatedGoogle: RequestHandler = (req, res, next) => {
  if (req.isAuthenticated()) {
    return next();
  }
  return res.status(401).json({ message: "Unauthorized" });
};

server/index.ts.content:
import 'dotenv/config';
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/replitAuth.ts.content:
import * as client from "openid-client";
import { Strategy, type VerifyFunction } from "openid-client/passport";

import passport from "passport";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import memoize from "memoizee";
import connectPg from "connect-pg-simple";
import { storage } from "./storage";

if (!process.env.REPLIT_DOMAINS) {
  throw new Error("Environment variable REPLIT_DOMAINS not provided");
}

const getOidcConfig = memoize(
  async () => {
    return await client.discovery(
      new URL(process.env.ISSUER_URL ?? "https://replit.com/oidc"),
      process.env.REPL_ID!
    );
  },
  { maxAge: 3600 * 1000 }
);

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const pgStore = connectPg(session);
  const sessionStore = new pgStore({
    conString: process.env.DATABASE_URL,
    createTableIfMissing: false,
    ttl: sessionTtl,
    tableName: "sessions",
  });
  return session({
    secret: process.env.SESSION_SECRET || 'dev-secret-key-change-in-production',
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: true,
      maxAge: sessionTtl,
    },
  });
}

function updateUserSession(
  user: any,
  tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers
) {
  user.claims = tokens.claims();
  user.access_token = tokens.access_token;
  user.refresh_token = tokens.refresh_token;
  user.expires_at = user.claims?.exp;
}

async function upsertUser(
  claims: any,
) {
  await storage.upsertUser({
    id: claims["sub"],
    email: claims["email"],
    firstName: claims["first_name"],
    lastName: claims["last_name"],
    profileImageUrl: claims["profile_image_url"],
  });
}

export async function setupAuth(app: Express) {
  app.set("trust proxy", 1);
  app.use(getSession());
  app.use(passport.initialize());
  app.use(passport.session());

  const config = await getOidcConfig();

  const verify: VerifyFunction = async (
    tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers,
    verified: passport.AuthenticateCallback
  ) => {
    const user = {};
    updateUserSession(user, tokens);
    await upsertUser(tokens.claims());
    verified(null, user);
  };

  for (const domain of process.env
    .REPLIT_DOMAINS!.split(",")) {
    const strategy = new Strategy(
      {
        name: `replitauth:${domain}`,
        config,
        scope: "openid email profile offline_access",
        callbackURL: `https://${domain}/api/callback`,
      },
      verify,
    );
    passport.use(strategy);
  }

  passport.serializeUser((user: Express.User, cb) => cb(null, user));
  passport.deserializeUser((user: Express.User, cb) => cb(null, user));

  app.get("/api/login", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      prompt: "login consent",
      scope: ["openid", "email", "profile", "offline_access"],
    })(req, res, next);
  });

  app.get("/api/callback", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      successReturnToOrRedirect: "/",
      failureRedirect: "/api/login",
    })(req, res, next);
  });

  app.get("/api/logout", (req, res) => {
    req.logout(() => {
      res.redirect(
        client.buildEndSessionUrl(config, {
          client_id: process.env.REPL_ID!,
          post_logout_redirect_uri: `${req.protocol}://${req.hostname}`,
        }).href
      );
    });
  });
}

export const isAuthenticated: RequestHandler = async (req, res, next) => {
  const user = req.user as any;

  if (!req.isAuthenticated() || !user.expires_at) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const now = Math.floor(Date.now() / 1000);
  if (now <= user.expires_at) {
    return next();
  }

  const refreshToken = user.refresh_token;
  if (!refreshToken) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }

  try {
    const config = await getOidcConfig();
    const tokenResponse = await client.refreshTokenGrant(config, refreshToken);
    updateUserSession(user, tokenResponse);
    return next();
  } catch (error) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }
};

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { WebSocketServer, WebSocket } from "ws";
import { storage } from "./storage";
import { aiService } from "./services/aiService";
import { newsService } from "./services/newsService";
// Authentication removed - open access
import { z } from "zod";

export async function registerRoutes(app: Express): Promise<Server> {
  // Authentication removed - open access app

  // Start news processing
  newsService.startPeriodicUpdate();

  // Public routes - no authentication required
  app.get('/api/auth/user', async (req: any, res) => {
    // Return a mock user for UI compatibility
    res.json({ 
      id: 'guest', 
      email: 'guest@example.com', 
      firstName: 'Guest', 
      lastName: 'User' 
    });
  });

  // Articles routes
  app.get('/api/articles', async (req: any, res) => {
    try {
      const userId = undefined; // No user tracking
      const { category, timeRange, search, page = 1, limit = 20 } = req.query;

      const articles = await storage.getArticles({
        category: category as string,
        timeRange: timeRange as string,
        search: search as string,
        page: parseInt(page as string),
        limit: parseInt(limit as string),
        userId,
      });

      res.json(articles);
    } catch (error) {
      console.error("Error fetching articles:", error);
      res.status(500).json({ message: "Failed to fetch articles" });
    }
  });

  app.get('/api/articles/:id', async (req: any, res) => {
    try {
      const userId = undefined; // No user tracking
      const { id } = req.params;

      const article = await storage.getArticleById(id, userId);
      if (!article) {
        return res.status(404).json({ message: "Article not found" });
      }

      // Increment view count
      await storage.incrementArticleViews(id);

      res.json(article);
    } catch (error) {
      console.error("Error fetching article:", error);
      res.status(500).json({ message: "Failed to fetch article" });
    }
  });

  app.get('/api/search', async (req: any, res) => {
    try {
      const userId = undefined; // No user tracking
      const { q } = req.query;

      if (!q || typeof q !== 'string') {
        return res.status(400).json({ message: "Search query is required" });
      }

      const articles = await storage.searchArticles(q, userId);
      res.json(articles);
    } catch (error) {
      console.error("Error searching articles:", error);
      res.status(500).json({ message: "Failed to search articles" });
    }
  });

  // Bookmarks routes - now public, no user tracking
  app.get('/api/bookmarks', async (req: any, res) => {
    try {
      // Return empty bookmarks for guest users
      res.json([]);
    } catch (error) {
      console.error("Error fetching bookmarks:", error);
      res.status(500).json({ message: "Failed to fetch bookmarks" });
    }
  });

  app.post('/api/bookmarks', async (req: any, res) => {
    try {
      // Bookmarks disabled for guest users
      res.status(200).json({ message: "Bookmark feature disabled in guest mode" });
    } catch (error) {
      console.error("Error creating bookmark:", error);
      res.status(500).json({ message: "Failed to create bookmark" });
    }
  });

  app.delete('/api/bookmarks/:articleId', async (req: any, res) => {
    try {
      // Bookmarks disabled for guest users
      res.status(200).json({ message: "Bookmark feature disabled in guest mode" });
    } catch (error) {
      console.error("Error deleting bookmark:", error);
      res.status(500).json({ message: "Failed to delete bookmark" });
    }
  });

  // AI Chat routes - now public
  app.post('/api/chat/summarize', async (req: any, res) => {
    try {
      const userId = 'guest'; // Guest user for AI features
      const { articleId, mode = 'medium' } = req.body;

      if (!articleId) {
        return res.status(400).json({ message: "Article ID is required" });
      }

      const summary = await aiService.summarizeArticle({
        articleId,
        mode: mode as 'short' | 'medium' | 'long',
        userId,
      });

      res.json({ summary });
    } catch (error) {
      console.error("Error summarizing article:", error);
      res.status(500).json({ message: "Failed to summarize article" });
    }
  });

  app.post('/api/chat/message', async (req: any, res) => {
    try {
      const userId = 'guest'; // Guest user for AI features
      const { articleId, message } = req.body;

      if (!articleId || !message) {
        return res.status(400).json({ message: "Article ID and message are required" });
      }

      // Get conversation history
      const conversationHistory = await aiService.getChatHistory(userId, articleId);

      const response = await aiService.chatAboutArticle({
        articleId,
        message,
        userId,
        conversationHistory,
      });

      res.json({ response });
    } catch (error) {
      console.error("Error processing chat message:", error);
      res.status(500).json({ message: "Failed to process chat message" });
    }
  });

  app.get('/api/chat/:articleId/history', async (req: any, res) => {
    try {
      const userId = 'guest'; // Guest user for AI features
      const { articleId } = req.params;

      const history = await aiService.getChatHistory(userId, articleId);
      res.json(history);
    } catch (error) {
      console.error("Error fetching chat history:", error);
      res.status(500).json({ message: "Failed to fetch chat history" });
    }
  });

  app.post('/api/chat/compare', async (req: any, res) => {
    try {
      const userId = 'guest'; // Guest user for AI features
      const { articleIds } = req.body;

      if (!Array.isArray(articleIds) || articleIds.length < 2) {
        return res.status(400).json({ message: "At least 2 article IDs are required" });
      }

      const comparison = await aiService.compareArticles(articleIds, userId);
      res.json({ comparison });
    } catch (error) {
      console.error("Error comparing articles:", error);
      res.status(500).json({ message: "Failed to compare articles" });
    }
  });

  // Trending routes
  app.get('/api/trending', async (req: any, res) => {
    try {
      const { range = 'today' } = req.query;
      const trending = await storage.getTrendingTopics(range as string);
      res.json(trending);
    } catch (error) {
      console.error("Error fetching trending topics:", error);
      res.status(500).json({ message: "Failed to fetch trending topics" });
    }
  });

  // Sources routes
  app.get('/api/sources', async (req: any, res) => {
    try {
      const sources = await storage.getSources();
      res.json(sources);
    } catch (error) {
      console.error("Error fetching sources:", error);
      res.status(500).json({ message: "Failed to fetch sources" });
    }
  });

  // Manual refresh route for testing
  app.post('/api/refresh', async (req: any, res) => {
    try {
      await newsService.processNewArticles();
      res.json({ message: "News refresh completed" });
    } catch (error) {
      console.error("Error refreshing news:", error);
      res.status(500).json({ message: "Failed to refresh news" });
    }
  });

  const httpServer = createServer(app);

  // WebSocket setup for real-time updates
  const wss = new WebSocketServer({ server: httpServer, path: '/ws' });

  wss.on('connection', (ws: WebSocket) => {
    console.log('WebSocket client connected');

    ws.on('message', (message: string) => {
      try {
        const data = JSON.parse(message);

        if (data.type === 'subscribe') {
          // Handle subscription to specific topics
          console.log('Client subscribed to:', data.topic);
        }
      } catch (error) {
        console.error('Error parsing WebSocket message:', error);
      }
    });

    ws.on('close', () => {
      console.log('WebSocket client disconnected');
    });

    // Send initial connection message
    if (ws.readyState === WebSocket.OPEN) {
      ws.send(JSON.stringify({ 
        type: 'connected', 
        message: 'Connected to TechPulse real-time updates' 
      }));
    }
  });

  // Broadcast updates to connected clients
  const broadcastUpdate = (type: string, data: any) => {
    wss.clients.forEach((client) => {
      if (client.readyState === WebSocket.OPEN) {
        client.send(JSON.stringify({ type, data }));
      }
    });
  };

  // Example: Broadcast when new articles are available
  setInterval(() => {
    broadcastUpdate('heartbeat', { timestamp: Date.now() });
  }, 30000); // Every 30 seconds

  return httpServer;
}

server/storage.ts.content:
import {
  users,
  articles,
  sources,
  bookmarks,
  chatConversations,
  trendingRecords,
  relatedArticles,
  type User,
  type UpsertUser,
  type Article,
  type InsertArticle,
  type ArticleWithSource,
  type ArticleWithRelated,
  type Source,
  type InsertSource,
  type Bookmark,
  type InsertBookmark,
  type ChatConversation,
  type InsertChatConversation,
  type TrendingRecord,
  type InsertTrendingRecord,
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, and, or, sql, like, inArray, gte, lte } from "drizzle-orm";

export interface IStorage {
  // User operations (required for Replit Auth)
  getUser(id: string): Promise<User | undefined>;
  upsertUser(user: UpsertUser): Promise<User>;

  // Source operations
  getSources(): Promise<Source[]>;
  createSource(source: InsertSource): Promise<Source>;
  updateSourceLastFetch(id: string): Promise<void>;

  // Article operations
  getArticles(params: {
    category?: string;
    timeRange?: string;
    search?: string;
    page?: number;
    limit?: number;
    userId?: string;
  }): Promise<ArticleWithRelated[]>;
  getArticleById(id: string, userId?: string): Promise<ArticleWithRelated | undefined>;
  createArticle(article: InsertArticle): Promise<Article>;
  updateArticle(id: string, updates: Partial<InsertArticle>): Promise<Article>;
  incrementArticleViews(id: string): Promise<void>;

  // Bookmark operations
  getBookmarks(userId: string): Promise<ArticleWithSource[]>;
  createBookmark(bookmark: InsertBookmark): Promise<Bookmark>;
  deleteBookmark(userId: string, articleId: string): Promise<void>;
  isBookmarked(userId: string, articleId: string): Promise<boolean>;

  // Chat operations
  getChatConversation(userId: string, articleId: string): Promise<ChatConversation | undefined>;
  createChatConversation(conversation: InsertChatConversation): Promise<ChatConversation>;
  updateChatConversation(id: string, messages: any[]): Promise<ChatConversation>;

  // Trending operations
  getTrendingTopics(timeRange?: string): Promise<TrendingRecord[]>;
  createTrendingRecord(record: InsertTrendingRecord): Promise<TrendingRecord>;

  // Related articles
  getRelatedArticles(articleId: string): Promise<ArticleWithSource[]>;
  createRelatedArticle(articleId: string, relatedArticleId: string, similarityScore: number): Promise<void>;

  // Search operations
  searchArticles(query: string, userId?: string): Promise<ArticleWithRelated[]>;
}

export class DatabaseStorage implements IStorage {
  // User operations
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .onConflictDoUpdate({
        target: users.id,
        set: {
          ...userData,
          updatedAt: new Date(),
        },
      })
      .returning();
    return user;
  }

  // Source operations
  async getSources(): Promise<Source[]> {
    return await db.select().from(sources).where(eq(sources.isActive, true));
  }

  async createSource(source: InsertSource): Promise<Source> {
    const [newSource] = await db.insert(sources).values(source).returning();
    return newSource;
  }

  async updateSourceLastFetch(id: string): Promise<void> {
    await db
      .update(sources)
      .set({ lastFetchAt: new Date() })
      .where(eq(sources.id, id));
  }

  // Article operations
  async getArticles(params: {
    category?: string;
    timeRange?: string;
    search?: string;
    page?: number;
    limit?: number;
    userId?: string;
  }): Promise<ArticleWithRelated[]> {
    const page = params.page || 1;
    const limit = params.limit || 20;
    const offset = (page - 1) * limit;

    let whereConditions = [];

    if (params.category && params.category !== 'all') {
      whereConditions.push(eq(articles.category, params.category as any));
    }

    if (params.timeRange) {
      const now = new Date();
      let timeThreshold: Date;

      switch (params.timeRange) {
        case 'today':
          timeThreshold = new Date(now.getTime() - 24 * 60 * 60 * 1000);
          break;
        case 'week':
          timeThreshold = new Date(now.getTime() - 7 * 24 * 60 * 60 * 1000);
          break;
        case 'month':
          timeThreshold = new Date(now.getTime() - 30 * 24 * 60 * 60 * 1000);
          break;
        default:
          timeThreshold = new Date(0);
      }

      whereConditions.push(gte(articles.publishedAt, timeThreshold));
    }

    if (params.search) {
      whereConditions.push(
        or(
          like(articles.title, `%${params.search}%`),
          like(articles.content, `%${params.search}%`)
        )
      );
    }

    const query = db
      .select({
        article: articles,
        source: sources,
        isBookmarked: params.userId 
          ? sql<boolean>`EXISTS(SELECT 1 FROM ${bookmarks} WHERE ${bookmarks.userId} = ${params.userId} AND ${bookmarks.articleId} = ${articles.id})`
          : sql<boolean>`false`,
      })
      .from(articles)
      .leftJoin(sources, eq(articles.sourceId, sources.id))
      .where(whereConditions.length > 0 ? and(...whereConditions) : undefined)
      .orderBy(desc(articles.publishedAt))
      .limit(params.limit || 20)
      .offset(offset);

    const results = await query;

    return results.map(row => ({
      ...row.article,
      source: row.source,
      isBookmarked: row.isBookmarked,
    }));
  }

  async getArticleById(id: string, userId?: string): Promise<ArticleWithRelated | undefined> {
    const query = db
      .select({
        article: articles,
        source: sources,
        isBookmarked: userId 
          ? sql<boolean>`EXISTS(SELECT 1 FROM ${bookmarks} WHERE ${bookmarks.userId} = ${userId} AND ${bookmarks.articleId} = ${articles.id})`
          : sql<boolean>`false`,
      })
      .from(articles)
      .leftJoin(sources, eq(articles.sourceId, sources.id))
      .where(eq(articles.id, id));

    const [result] = await query;

    if (!result) return undefined;

    const relatedArticles = await this.getRelatedArticles(id);

    return {
      ...result.article,
      source: result.source,
      isBookmarked: result.isBookmarked,
      relatedArticles,
    };
  }

  async createArticle(article: InsertArticle): Promise<Article> {
    const [newArticle] = await db.insert(articles).values(article).returning();
    return newArticle;
  }

  async updateArticle(id: string, updates: Partial<InsertArticle>): Promise<Article> {
    const [updatedArticle] = await db
      .update(articles)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(articles.id, id))
      .returning();
    return updatedArticle;
  }

  async incrementArticleViews(id: string): Promise<void> {
    await db
      .update(articles)
      .set({ viewCount: sql`${articles.viewCount} + 1` })
      .where(eq(articles.id, id));
  }

  // Bookmark operations
  async getBookmarks(userId: string): Promise<ArticleWithSource[]> {
    const results = await db
      .select({
        article: articles,
        source: sources,
      })
      .from(bookmarks)
      .innerJoin(articles, eq(bookmarks.articleId, articles.id))
      .leftJoin(sources, eq(articles.sourceId, sources.id))
      .where(eq(bookmarks.userId, userId))
      .orderBy(desc(bookmarks.savedAt));

    return results.map(row => ({
      ...row.article,
      source: row.source,
    }));
  }

  async createBookmark(bookmark: InsertBookmark): Promise<Bookmark> {
    const [newBookmark] = await db.insert(bookmarks).values(bookmark).returning();
    return newBookmark;
  }

  async deleteBookmark(userId: string, articleId: string): Promise<void> {
    await db
      .delete(bookmarks)
      .where(and(eq(bookmarks.userId, userId), eq(bookmarks.articleId, articleId)));
  }

  async isBookmarked(userId: string, articleId: string): Promise<boolean> {
    const [result] = await db
      .select({ count: sql<number>`count(*)` })
      .from(bookmarks)
      .where(and(eq(bookmarks.userId, userId), eq(bookmarks.articleId, articleId)));

    return result.count > 0;
  }

  // Chat operations
  async getChatConversation(userId: string, articleId: string): Promise<ChatConversation | undefined> {
    const [conversation] = await db
      .select()
      .from(chatConversations)
      .where(and(eq(chatConversations.userId, userId), eq(chatConversations.articleId, articleId)));

    return conversation;
  }

  async createChatConversation(conversation: InsertChatConversation): Promise<ChatConversation> {
    const [newConversation] = await db
      .insert(chatConversations)
      .values(conversation)
      .returning();
    return newConversation;
  }

  async updateChatConversation(id: string, messages: any[]): Promise<ChatConversation> {
    const [updatedConversation] = await db
      .update(chatConversations)
      .set({ 
        messages: JSON.stringify(messages), 
        lastActiveAt: new Date() 
      })
      .where(eq(chatConversations.id, id))
      .returning();
    return updatedConversation;
  }

  // Trending operations
  async getTrendingTopics(timeRange = 'today'): Promise<TrendingRecord[]> {
    const now = new Date();
    let timeThreshold: Date;

    switch (timeRange) {
      case 'today':
        timeThreshold = new Date(now.getTime() - 24 * 60 * 60 * 1000);
        break;
      case 'week':
        timeThreshold = new Date(now.getTime() - 7 * 24 * 60 * 60 * 1000);
        break;
      default:
        timeThreshold = new Date(now.getTime() - 24 * 60 * 60 * 1000);
    }

    return await db
      .select()
      .from(trendingRecords)
      .where(gte(trendingRecords.date, timeThreshold))
      .orderBy(desc(trendingRecords.growthRate))
      .limit(10);
  }

  async createTrendingRecord(record: InsertTrendingRecord): Promise<TrendingRecord> {
    const [newRecord] = await db.insert(trendingRecords).values(record).returning();
    return newRecord;
  }

  // Related articles
  async getRelatedArticles(articleId: string): Promise<ArticleWithSource[]> {
    const results = await db
      .select({
        article: articles,
        source: sources,
      })
      .from(relatedArticles)
      .innerJoin(articles, eq(relatedArticles.relatedArticleId, articles.id))
      .leftJoin(sources, eq(articles.sourceId, sources.id))
      .where(eq(relatedArticles.articleId, articleId))
      .orderBy(desc(relatedArticles.similarityScore))
      .limit(5);

    return results.map(row => ({
      ...row.article,
      source: row.source,
    }));
  }

  async createRelatedArticle(articleId: string, relatedArticleId: string, similarityScore: number): Promise<void> {
    await db.insert(relatedArticles).values({
      articleId,
      relatedArticleId,
      similarityScore,
    });
  }

  // Search operations
  async searchArticles(query: string, userId?: string): Promise<ArticleWithRelated[]> {
    const searchCondition = or(
      like(articles.title, `%${query}%`),
      like(articles.content, `%${query}%`),
      like(articles.snippet, `%${query}%`)
    );

    const results = await db
      .select({
        article: articles,
        source: sources,
        isBookmarked: userId 
          ? sql<boolean>`EXISTS(SELECT 1 FROM ${bookmarks} WHERE ${bookmarks.userId} = ${userId} AND ${bookmarks.articleId} = ${articles.id})`
          : sql<boolean>`false`,
      })
      .from(articles)
      .leftJoin(sources, eq(articles.sourceId, sources.id))
      .where(searchCondition)
      .orderBy(desc(articles.publishedAt))
      .limit(50);

    return results.map(row => ({
      ...row.article,
      source: row.source,
      isBookmarked: row.isBookmarked,
    }));
  }
}

export const storage = new DatabaseStorage();

