  --popover-foreground: hsl(200, 6.6667%, 91.1765%);
  --primary: hsl(147, 100%, 35%);
  --primary-foreground: hsl(0, 0%, 100%);
  --secondary: hsl(147, 30%, 25%);
  --secondary-foreground: hsl(147, 100%, 85%);
  --muted: hsl(210, 25%, 8%);
  --muted-foreground: hsl(210, 3.3898%, 46.2745%);
  --accent: hsl(210, 25%, 15%);
  --accent-foreground: hsl(147, 100%, 65%);
  --destructive: hsl(356.3033, 90.5579%, 54.3137%);
  --destructive-foreground: hsl(0, 0%, 100%);
  --border: hsl(210, 25%, 18%);
  --input: hsl(210, 25%, 20%);
  --ring: hsl(147, 100%, 35%);
  --chart-1: hsl(147, 100%, 35%);
  --chart-2: hsl(147, 80%, 45%);
  --chart-3: hsl(42.0290, 92.8251%, 56.2745%);
  --chart-4: hsl(147.1429, 78.5047%, 41.9608%);
  --chart-5: hsl(341.4894, 75.2000%, 50.9804%);
  --sidebar: hsl(210, 25%, 12%);
  --sidebar-foreground: hsl(0, 0%, 85.0980%);
  --sidebar-primary: hsl(147, 100%, 35%);
  --sidebar-primary-foreground: hsl(0, 0%, 100%);
  --sidebar-accent: hsl(210, 25%, 12%);
  --sidebar-accent-foreground: hsl(203.7736, 87.6033%, 52.5490%);
  --sidebar-border: hsl(210, 25%, 25%);
  --sidebar-ring: hsl(202.8169, 89.1213%, 53.1373%);
  --tech-blue: hsl(203.8863, 88.2845%, 53.1373%);
  --tech-green: hsl(159.7826, 100%, 36.0784%);
  --slate-850: hsl(210, 25%, 15%);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    font-family: var(--font-sans);
    line-height: 1.6;
  }
}

/* Mobile-first responsive design improvements */
@layer utilities {
  .scrollbar-hide {
    -ms-overflow-style: none;
    scrollbar-width: none;
  }

  .scrollbar-hide::-webkit-scrollbar {
    display: none;
  }

  /* Custom breakpoint for extra small devices */
  @media (min-width: 475px) {
    .xs\:flex {
      display: flex;
    }

    .xs\:hidden {
      display: none;
    }
  }
}

@layer base {
  h1, h2, h3, h4, h5, h6 {
    @apply font-semibold;
    line-height: 1.3;
  }

  p {
    @apply leading-relaxed;
  }
}

@layer components {
  .tech-gradient {
    background: linear-gradient(135deg, var(--tech-blue) 0%, var(--tech-green) 100%);
  }

  .line-clamp-2 {
    display: -webkit-box;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
    overflow: hidden;
  }

  .line-clamp-3 {
    display: -webkit-box;
    -webkit-line-clamp: 3;
    -webkit-box-orient: vertical;
    overflow: hidden;
  }
}

@layer utilities {
  .text-tech-blue {
    color: var(--tech-blue);
  }

  .text-tech-green {
    color: var(--tech-green);
  }

  .bg-tech-blue {
    background-color: var(--tech-blue);
  }

  .bg-tech-green {
    background-color: var(--tech-green);
  }

  .bg-slate-850 {
    background-color: var(--slate-850);
  }

  /* Mobile-first responsive utilities */
  .mobile-safe-padding {
    padding-left: max(1rem, env(safe-area-inset-left));
    padding-right: max(1rem, env(safe-area-inset-right));
  }

  .touch-manipulation {
    touch-action: manipulation;
  }

  /* Skeleton loading animations */
  .skeleton {
    @apply animate-pulse bg-muted;
  }

  .border-tech-blue {
    border-color: var(--tech-blue);
  }

  .hover\:bg-tech-blue:hover {
    background-color: var(--tech-blue);
  }

  .focus\:ring-tech-blue:focus {
    --tw-ring-color: var(--tech-blue);
  }
}

