import { storage } from "../storage";
import { aiService } from "./aiService";
import { rssService } from "./rssService";
import { InsertTrendingRecord } from "@shared/schema";

export class NewsService {
  async processNewArticles(): Promise<void> {
    console.log('Starting news processing...');

    // Initialize default sources if needed
    await rssService.initializeDefaultSources();

    // Fetch articles from all sources
    await rssService.fetchAllSources();

    // Process sentiment analysis for recent articles
    await this.processSentimentAnalysis();

    // Generate trending topics
    await this.updateTrendingTopics();

    console.log('News processing completed');
  }

  private async processSentimentAnalysis(): Promise<void> {
    try {
      // Get recent articles without sentiment analysis
      const recentArticles = await storage.getArticles({ 
        timeRange: 'today', 
        limit: 50 
      });

      for (const article of recentArticles) {
        if (article.sentiment || !article.content) continue;

        try {
          const sentimentResult = await aiService.analyzeSentiment(
            article.title + '\n' + (article.content || article.snippet || '')
          );

          await storage.updateArticle(article.id, {
            sentiment: sentimentResult.sentiment,
            sentimentScore: sentimentResult.score,
          });

          console.log(`Processed sentiment for: ${article.title}`);
        } catch (error) {
          console.error(`Error processing sentiment for article ${article.id}:`, error);
        }
      }
    } catch (error) {
      console.error('Error in sentiment processing:', error);
    }
  }

  private async updateTrendingTopics(): Promise<void> {
    try {
      const recentArticles = await storage.getArticles({ 
        timeRange: 'today', 
        limit: 100 
      });

      // Extract keywords and count frequency
      const keywordCounts = new Map<string, number>();
      const categoryTopics = new Map<string, Map<string, number>>();

      for (const article of recentArticles) {
        // Extract keywords from title and content
        const text = (article.title + ' ' + (article.snippet || '')).toLowerCase();
        const keywords = this.extractKeywords(text);

        // Initialize category map if needed
        if (!categoryTopics.has(article.category)) {
          categoryTopics.set(article.category, new Map());
        }
        const categoryMap = categoryTopics.get(article.category)!;

        for (const keyword of keywords) {
          keywordCounts.set(keyword, (keywordCounts.get(keyword) || 0) + 1);
          categoryMap.set(keyword, (categoryMap.get(keyword) || 0) + 1);
        }
      }

      // Store trending topics
      const today = new Date();
      today.setHours(0, 0, 0, 0);

      // Top overall trending topics
      const sortedKeywords = Array.from(keywordCounts.entries())
        .sort(([, a], [, b]) => b - a)
        .slice(0, 20);

      for (const [topic, count] of sortedKeywords) {
        if (count >= 3) { // Only topics mentioned at least 3 times
          const growthRate = this.calculateGrowthRate(topic, count);

          const trendingRecord: InsertTrendingRecord = {
            date: today,
            topic,
            count,
            growthRate,
          };

          await storage.createTrendingRecord(trendingRecord);
        }
      }

      // Category-specific trending topics
      for (const [category, topics] of categoryTopics) {
        const sortedCategoryTopics = Array.from(topics.entries())
          .sort(([, a], [, b]) => b - a)
          .slice(0, 10);

        for (const [topic, count] of sortedCategoryTopics) {
          if (count >= 2) {
            const growthRate = this.calculateGrowthRate(topic, count);

            const trendingRecord: InsertTrendingRecord = {
              date: today,
              topic,
              count,
              category: category as any,
              growthRate,
            };

            await storage.createTrendingRecord(trendingRecord);
          }
        }
      }

      console.log(`Updated trending topics: ${sortedKeywords.length} overall topics`);
    } catch (error) {
      console.error('Error updating trending topics:', error);
    }
  }

  private extractKeywords(text: string): string[] {
    // Common tech keywords and phrases
    const techKeywords = [
      'ai', 'artificial intelligence', 'machine learning', 'ml', 'deep learning',
      'openai', 'gpt', 'chatgpt', 'claude', 'llm', 'neural network',
      'blockchain', 'crypto', 'bitcoin', 'ethereum', 'web3', 'nft',
      'startup', 'funding', 'venture capital', 'ipo', 'acquisition',
      'cybersecurity', 'security', 'hack', 'breach', 'vulnerability',
      'mobile', 'iphone', 'android', 'app', 'ios',
      'cloud', 'aws', 'azure', 'google cloud', 'saas',
      'apple', 'google', 'microsoft', 'meta', 'tesla', 'nvidia',
      'quantum', 'robotics', 'automation', 'iot', 'ar', 'vr',
      'privacy', 'data', 'algorithm', 'software', 'hardware',
      'api', 'opensource', 'developer', 'programming'
    ];

    const words = text.match(/\b\w+\b/g) || [];
    const keywords: string[] = [];

    // Extract tech keywords
    for (const keyword of techKeywords) {
      if (text.includes(keyword)) {
        keywords.push(keyword);
      }
    }

    // Extract company names (capitalized words)
    const companyPattern = /\b[A-Z][a-z]+(?:\s+[A-Z][a-z]+)*\b/g;
    const companies = text.match(companyPattern) || [];
    keywords.push(...companies.map(c => c.toLowerCase()));

    // Remove duplicates and return
    return [...new Set(keywords)];
  }

  private calculateGrowthRate(topic: string, currentCount: number): number {
    // Simple growth rate calculation - in production, compare with historical data
    // For now, use count as a proxy for growth rate
    return Math.min(100, (currentCount - 1) * 10);
  }

  async generateRelatedArticles(): Promise<void> {
    try {
      const recentArticles = await storage.getArticles({ 
        timeRange: 'week', 
        limit: 100 
      });

      for (const article of recentArticles) {
        if (!article.content) continue;

        // Generate embedding for the article
        const embedding = await aiService.generateEmbedding(
          article.title + '\n' + (article.content || article.snippet || '')
        );

        if (embedding.length > 0) {
          // Update article with embedding
          await storage.updateArticle(article.id, {
            embedding: JSON.stringify(embedding),
          });

          // Find similar articles (simplified similarity check)
          const potentialRelated = recentArticles.filter(other => 
            other.id !== article.id && 
            other.category === article.category
          );

          // For now, use category matching - in production, use embedding similarity
          for (const related of potentialRelated.slice(0, 5)) {
            const similarityScore = this.calculateSimpleSimilarity(article, related);

            if (similarityScore > 0.3) {
              await storage.createRelatedArticle(article.id, related.id, similarityScore);
            }
          }
        }
      }

      console.log('Generated related articles');
    } catch (error) {
      console.error('Error generating related articles:', error);
    }
  }

  private calculateSimpleSimilarity(article1: any, article2: any): number {
    // Simple similarity based on common words in titles
    const words1 = new Set(article1.title.toLowerCase().split(/\s+/));
    const words2 = new Set(article2.title.toLowerCase().split(/\s+/));

    const intersection = new Set([...words1].filter(x => words2.has(x)));
    const union = new Set([...words1, ...words2]);

    return intersection.size / union.size;
  }

  async startPeriodicUpdate(): Promise<void> {
    // Initial processing
    await this.processNewArticles();

    // Set up periodic updates
    setInterval(async () => {
      try {
        await this.processNewArticles();
      } catch (error) {
        console.error('Error in periodic update:', error);
      }
    }, 5 * 60 * 1000); // Every 5 minutes

    // Generate related articles every hour
    setInterval(async () => {
      try {
        await this.generateRelatedArticles();
      } catch (error) {
        console.error('Error generating related articles:', error);
      }
    }, 60 * 60 * 1000); // Every hour
  }
}

export const newsService = new NewsService();

server/services/rssService.ts.content:
import { InsertArticle, InsertSource } from "@shared/schema";
import { storage } from "../storage";

interface RSSItem {
  title: string;
  link: string;
  description?: string;
  pubDate?: string;
  content?: string;
  guid?: string;
}

interface RSSFeed {
  title: string;
  items: RSSItem[];
}

export class RSSService {
  private async fetchRSS(url: string): Promise<RSSFeed> {
    try {
      const response = await fetch(url);
      if (!response.ok) {
        throw new Error(`Failed to fetch RSS: ${response.statusText}`);
      }

      const xmlText = await response.text();
      return this.parseRSS(xmlText);
    } catch (error) {
      console.error(`Error fetching RSS from ${url}:`, error);
      throw error;
    }
  }

  private parseRSS(xmlText: string): RSSFeed {
    // Basic RSS parsing - in production, use a proper XML parser
    const titleMatch = xmlText.match(/<title><!\[CDATA\[(.*?)\]\]><\/title>|<title>(.*?)<\/title>/i);
    const title = titleMatch ? (titleMatch[1] || titleMatch[2]) : 'Unknown Feed';

    const items: RSSItem[] = [];
    const itemRegex = /<item>(.*?)<\/item>/gis;
    let itemMatch;

    while ((itemMatch = itemRegex.exec(xmlText)) !== null) {
      const itemContent = itemMatch[1];

      const itemTitleMatch = itemContent.match(/<title><!\[CDATA\[(.*?)\]\]><\/title>|<title>(.*?)<\/title>/i);
      const itemTitle = itemTitleMatch ? (itemTitleMatch[1] || itemTitleMatch[2]) : '';

      const linkMatch = itemContent.match(/<link>(.*?)<\/link>/i);
      const link = linkMatch ? linkMatch[1].trim() : '';

      const descMatch = itemContent.match(/<description><!\[CDATA\[(.*?)\]\]><\/description>|<description>(.*?)<\/description>/i);
      const description = descMatch ? (descMatch[1] || descMatch[2]) : '';

      const pubDateMatch = itemContent.match(/<pubDate>(.*?)<\/pubDate>/i);
      const pubDate = pubDateMatch ? pubDateMatch[1] : '';

      const guidMatch = itemContent.match(/<guid[^>]*>(.*?)<\/guid>/i);
      const guid = guidMatch ? guidMatch[1] : '';

      if (itemTitle && link) {
        items.push({
          title: this.cleanHTML(itemTitle),
          link: link,
          description: this.cleanHTML(description),
          pubDate: pubDate,
          guid: guid,
          content: this.cleanHTML(description),
        });
      }
    }

    return { title, items };
  }

  private cleanHTML(html: string): string {
    if (!html) return '';

    return html
      .replace(/<[^>]*>/g, '')
      .replace(/&quot;/g, '"')
      .replace(/&amp;/g, '&')
      .replace(/&lt;/g, '<')
      .replace(/&gt;/g, '>')
      .replace(/&nbsp;/g, ' ')
      .replace(/\s+/g, ' ')
      .trim();
  }

  private categorizeArticle(title: string, content: string): string {
    const text = (title + ' ' + content).toLowerCase();

    if (text.includes('ai') || text.includes('artificial intelligence') || text.includes('machine learning') || text.includes('ml') || text.includes('gpt') || text.includes('neural') || text.includes('openai')) {
      return 'ai_ml';
    }

    if (text.includes('startup') || text.includes('funding') || text.includes('venture') || text.includes('investment') || text.includes('y combinator')) {
      return 'startups';
    }

    if (text.includes('security') || text.includes('cyber') || text.includes('hack') || text.includes('vulnerability') || text.includes('breach') || text.includes('malware')) {
      return 'cybersecurity';
    }

    if (text.includes('mobile') || text.includes('iphone') || text.includes('android') || text.includes('app') || text.includes('smartphone')) {
      return 'mobile';
    }

    if (text.includes('web3') || text.includes('blockchain') || text.includes('crypto') || text.includes('bitcoin') || text.includes('ethereum') || text.includes('nft')) {
      return 'web3';
    }

    return 'others';
  }

  private createSnippet(content: string): string {
    if (!content) return '';

    const cleanContent = this.cleanHTML(content);
    return cleanContent.length > 200 
      ? cleanContent.substring(0, 200).trim() + '...'
      : cleanContent;
  }

  async fetchAndStoreArticles(sourceId: string, rssUrl: string): Promise<number> {
    try {
      console.log(`Fetching RSS from: ${rssUrl}`);
      const feed = await this.fetchRSS(rssUrl);
      let newArticlesCount = 0;

      for (const item of feed.items) {
        try {
          // Check if article already exists
          const existingArticles = await storage.getArticles({ search: item.title, limit: 1 });
          if (existingArticles.length > 0) {
            continue; // Skip if already exists
          }

          const publishedAt = item.pubDate ? new Date(item.pubDate) : new Date();
          const category = this.categorizeArticle(item.title, item.description || '');
          const snippet = this.createSnippet(item.description || '');

          const articleData: InsertArticle = {
            title: item.title,
            url: item.link,
            content: item.content || item.description || '',
            snippet: snippet,
            sourceId: sourceId,
            publishedAt: publishedAt,
            category: category as any,
            fetchedAt: new Date(),
          };

          await storage.createArticle(articleData);
          newArticlesCount++;
        } catch (error) {
          console.error(`Error saving article "${item.title}":`, error);
        }
      }

      // Update source last fetch time
      await storage.updateSourceLastFetch(sourceId);

      console.log(`Fetched ${newArticlesCount} new articles from ${rssUrl}`);
      return newArticlesCount;
    } catch (error) {
      console.error(`Error in fetchAndStoreArticles for ${rssUrl}:`, error);
      return 0;
    }
  }

  async initializeDefaultSources(): Promise<void> {
    const defaultSources = [
      {
        name: 'TechCrunch',
        url: 'https://techcrunch.com',
        rssUrl: 'https://techcrunch.com/feed/',
        isActive: true,
        fetchInterval: 300,
      },
      {
        name: 'The Verge',
        url: 'https://theverge.com',
        rssUrl: 'https://www.theverge.com/rss/index.xml',
        isActive: true,
        fetchInterval: 300,
      },
      {
        name: 'Hacker News',
        url: 'https://news.ycombinator.com',
        rssUrl: 'https://news.ycombinator.com/rss',
        isActive: true,
        fetchInterval: 600,
      },
      {
        name: 'Ars Technica',
        url: 'https://arstechnica.com',
        rssUrl: 'https://feeds.arstechnica.com/arstechnica/index',
        isActive: true,
        fetchInterval: 300,
      },
    ];

    const existingSources = await storage.getSources();

    for (const sourceData of defaultSources) {
      const exists = existingSources.find(s => s.name === sourceData.name);
      if (!exists) {
        await storage.createSource(sourceData);
        console.log(`Created default source: ${sourceData.name}`);
      }
    }
  }

  async fetchAllSources(): Promise<void> {
    const sources = await storage.getSources();

    for (const source of sources) {
      if (!source.rssUrl || !source.isActive) continue;

      try {
        const newArticles = await this.fetchAndStoreArticles(source.id, source.rssUrl);
        console.log(`Source ${source.name}: ${newArticles} new articles`);
      } catch (error) {
        console.error(`Error fetching from ${source.name}:`, error);
      }
    }
  }
}

export const rssService = new RSSService();

